$(document).ready(function(){$(".scroll").click(function(e){e.preventDefault();$("html,body").animate({scrollTop:$(this.hash).offset().top},500)});$(".parallax").stellar()});$(window).scroll(function(){var e=$(window).scrollTop(),t=$(document).height()-($(window).scrollTop()+$(window).height());if(t===0){$("nav a.active").removeClass("active");$("nav a:last").addClass("active")}else $("section").each(function(t){if($(this).position().top<=e+150){$("nav a.active").removeClass("active");$("nav a").eq(t).addClass("active")}})}).scroll();